class ConcessionariaCreateView(CreateView): model = User form_class = ConcessionariaCreateForm template_name = 'Concessionaria/registration_form.html' success_url = reverse_lazy('home')
def form_valid(self, form): user = form.save(commit=False) # Creazione dell'utente e della concessionaria user.set_password(form.cleaned_data['password']) user = form.save() group, created = Group.objects.get_or_create(name='concessionaria') user.groups.add(group) Concessionaria.objects.create( user=user, partita_iva = form.cleaned_data['partita_iva'], codice_fiscale = form.cleaned_data['codice_fiscale'] ) # Autenticazione e login automatico dopo la registrazione user = authenticate(username=form.cleaned_data['username'], password=form.cleaned_data['password'], email=form.cleaned_data['email']) if user is not None: login(self.request, user) return super().form_valid(form)
def form_invalid(self, form): messages.error(self.request, "Errore durante la registrazione. Controlla i dati inseriti.") return super().form_invalid(form)
