class ConcessionariaDeleteView(DeleteView): model = Concessionaria template_name = 'Concessionaria/concessionaria_confirm_delete.html' slug_field = 'slug' slug_url_kwarg = 'slug' success_url = reverse_lazy('home')
def delete(self, request, *args, **kwargs): self.object = self.get_object() user = self.object.user # Elimina eventuali oggetti collegati (es. Auto) Auto.objects.filter(user_auto=user).delete() user.delete() logout(request) request.session.flush() return redirect(self.success_url)
def post(self, request, *args, **kwargs): return self.delete(request, *args, **kwargs)
