@login_required
def user_autos_view(request):
    autos = Auto.objects.filter(user_auto=request.user)
    # Filtri da GET
    marca = request.GET.get('marca', '').strip()
    modello = request.GET.get('modello', '').strip()
    anno = request.GET.get('anno', '').strip()
    chilometri = request.GET.get('chilometri', '').strip()
    cilindrata = request.GET.get('cilindrata', '').strip()
    carburante = request.GET.get('carburante', '').strip()
    trazione = request.GET.get('trazione', '').strip()
    disponibilita = request.GET.get('disponibilita', '').strip()

    if marca:
        autos = autos.filter(marca__icontains=marca)
    if modello:
        autos = autos.filter(modello__icontains=modello)
    if anno:
        autos = autos.filter(anno=anno)
    if chilometri:
        autos = autos.filter(chilometraggio=chilometri)
    if cilindrata:
        autos = autos.filter(cilindrata=cilindrata)
    if carburante:
        autos = autos.filter(carburante=carburante)
    if trazione:
        autos = autos.filter(trazione=trazione)
    if disponibilita:
        autos = autos.filter(disponibilita=disponibilita)

    paginator = Paginator(autos, 9)  # 9 auto per pagina
    page_number = request.GET.get('page')
    autos_page = paginator.get_page(page_number)

    is_utente = request.user.groups.filter(name='utente').exists()
    is_concessionaria = request.user.groups.filter(name='concessionaria').exists()
    form = AddAutoForm()
    return render(request, 'Auto/user_autos.html', {
        'autos': autos_page,
        'is_utente': is_utente,
        'is_concessionaria': is_concessionaria,
        'form': form,
        'TIPOLOGIE_CARBURANTE': TIPOLOGIE_CARBURANTE,
        'TIPOLOGIE_TRAZIONE': TIPOLOGIE_TRAZIONE,
        'DISPONIBILITA': DISPONIBILITA
    })

from django import forms
from django.contrib.auth import authenticate
from django.contrib.auth.models import User
from django.core.exceptions import ValidationError

from .models import Concessionaria

class ConcessionariaLoginForm(forms.Form):
    email = forms.EmailField(label='Email', widget=forms.EmailInput(attrs={'class': 'form-control', 'placeholder': ''}))
    password = forms.CharField(widget=forms.PasswordInput(attrs={'class': 'form-control', 'placeholder': ''}))

    def clean(self):
        cleaned_data = super().clean()
        email = cleaned_data.get('email')
        password = cleaned_data.get('password')
        if email and password:
            user = authenticate(email=email, password=password)
            if user is None:
                raise forms.ValidationError('Email o password non validi')
            cleaned_data['user'] = user
        return cleaned_data

    def get_user(self):
        return self.cleaned_data.get('user', None)

class ConcessionariaForm(forms.ModelForm):
    email = forms.EmailField(widget=forms.EmailInput(attrs={'class': 'form-control', 'placeholder': ''}), required=True)
    username = forms.CharField(widget=forms.TextInput(attrs={'class': 'form-control', 'placeholder': ''}), required=True)
    password = forms.CharField(widget=forms.PasswordInput(attrs={'class': 'form-control', 'placeholder': ''}), required=True)

    class Meta:
        model = Concessionaria
        fields = ['username', 'email', 'password', 'partita_iva', 'codice_fiscale']
        widgets = {
            'partita_iva': forms.TextInput(attrs={'class': 'form-control', 'placeholder': ''}),
            'codice_fiscale': forms.TextInput(attrs={'class': 'form-control', 'placeholder': ''}),
        }

    def save(self, commit=True):
        user = User.objects.create_user(
            username=self.cleaned_data['username'],
            email=self.cleaned_data['email'],
            password=self.cleaned_data['password']
        )
        concessionaria = super().save(commit=False)
        concessionaria.user = user
        if commit:
            concessionaria.save()
        return concessionaria

class ConcessionariaUpdateForm(forms.ModelForm):
    class Meta:
        model = Concessionaria
        fields = ['partita_iva', 'codice_fiscale']
        widgets = {
            'partita_iva': forms.TextInput(attrs={'class': 'form-control', 'placeholder': ''}),
            'codice_fiscale': forms.TextInput(attrs={'class': 'form-control', 'placeholder': ''}),
        }

class ConcessionariaCreateForm(forms.ModelForm):
    password = forms.CharField(widget=forms.PasswordInput, label="Password")
    conferma_password = forms.CharField(widget=forms.PasswordInput, label="Conferma Password")
    partita_iva = forms.CharField(max_length=11, label="Partita IVA")
    codice_fiscale = forms.CharField(max_length=16, label="Codice Fiscale")

    class Meta:
        model = User
        fields = ['username', 'email', 'password']

    def clean(self):
        cleaned_data = super().clean()
        password = cleaned_data.get('password')
        conferma_password = cleaned_data.get('conferma_password')
        if password != conferma_password:
            raise ValidationError("Le password non corrispondono.")
        return cleaned_data

    def clean_username(self):
        username = self.cleaned_data.get('username')
        if User.objects.filter(username=username).exists():
            raise ValidationError("Il nome utente è già in uso. Scegli un altro nome utente.")
        return username

    def save(self, commit=True):
        user = super().save(commit=True)
        Concessionaria.objects.create(
            user=user,
            partita_iva=self.cleaned_data['partita_iva'],
            codice_fiscale=self.cleaned_data['codice_fiscale']
        )
        return user

from django import forms
from django.contrib.auth import authenticate
from django.contrib.auth.models import User
from django.core.exceptions import ValidationError

from .models import Concessionaria

class ConcessionariaLoginForm(forms.Form):
    email = forms.EmailField(label='Email', widget=forms.EmailInput(attrs={'class': 'form-control', 'placeholder': ''}))
    password = forms.CharField(widget=forms.PasswordInput(attrs={'class': 'form-control', 'placeholder': ''}))

    def clean(self):
        cleaned_data = super().clean()
        email = cleaned_data.get('email')
        password = cleaned_data.get('password')
        if email and password:
            user = authenticate(email=email, password=password)
            if user is None:
                raise forms.ValidationError('Email o password non validi')
            cleaned_data['user'] = user
        return cleaned_data

    def get_user(self):
        return self.cleaned_data.get('user', None)

class ConcessionariaForm(forms.ModelForm):
    email = forms.EmailField(widget=forms.EmailInput(attrs={'class': 'form-control', 'placeholder': ''}), required=True)
    username = forms.CharField(widget=forms.TextInput(attrs={'class': 'form-control', 'placeholder': ''}), required=True)
    password = forms.CharField(widget=forms.PasswordInput(attrs={'class': 'form-control', 'placeholder': ''}), required=True)

    class Meta:
        model = Concessionaria
        fields = ['username', 'email', 'password', 'partita_iva', 'codice_fiscale']
        widgets = {
            'partita_iva': forms.TextInput(attrs={'class': 'form-control', 'placeholder': ''}),
            'codice_fiscale': forms.TextInput(attrs={'class': 'form-control', 'placeholder': ''}),
        }

    def save(self, commit=True):
        user = User.objects.create_user(
            username=self.cleaned_data['username'],
            email=self.cleaned_data['email'],
            password=self.cleaned_data['password']
        )
        concessionaria = super().save(commit=False)
        concessionaria.user = user
        if commit:
            concessionaria.save()
        return concessionaria

class ConcessionariaUpdateForm(forms.ModelForm):
    class Meta:
