<script>
    function togglePrezzi() {
        var disp = document.getElementById('id_disponibilita').value;
        var vendita = document.getElementById('prezzo-vendita-group');
        var affitto = document.getElementById('prezzo-affitto-group');
        var venditaInput = document.getElementById('id_prezzo_vendita');
        var affittoInput = document.getElementById('id_prezzo_affitto');
        if (disp === '0') {
            vendita.style.display = '';
            affitto.style.display = 'none';
            venditaInput.required = true;
            affittoInput.required = false;
            affittoInput.value = '';
        } else if (disp === '1') {
            vendita.style.display = 'none';
            affitto.style.display = '';
            venditaInput.required = false;
            affittoInput.required = true;
            venditaInput.value = '';
        } else if (disp === '2') {
            vendita.style.display = '';
            affitto.style.display = '';
            venditaInput.required = true;
            affittoInput.required = true;
        }
    }
    document.getElementById('id_disponibilita').addEventListener('change', togglePrezzi);
    window.addEventListener('DOMContentLoaded', togglePrezzi);
</script>
{% endblock %}

